#!/bin/bash

# This script just sends rpc 'post_waku_v2_relay_v1_message' requests to
# http://localhost:8546

# Therefore, there should be an `nwaku` node listening to rpc requests.

# The number of messages is specified by the first parameter.

target='http://localhost:8546'

for i in $( seq $1 )
do
  echo $i
  curl -d "{\"jsonrpc\":\"2.0\",\"id\":"$(date +%s%N)",\"method\":\"post_waku_v2_relay_v1_message\", \"params\":[\"/waku/2/default-waku/proto\", {\"timestamp\":"$(date +%s%N)", \"payload\":\"\"}]}" --header "Content-Type: application/json" ${target}

  # sleep 1
done
